<template>
	<view id="follow">
		<view class="page-top"></view>
		<!--  -->
		<view  class="bootom-l-btn" @click="share">获取邀请码</view>
		<!--  -->
		<view class="content">
			<view v-if="person" class="title">当前被邀请人</view>
			<view v-if="person" class="list">
				<view class="item display-between-center">
					<view class="left">
						<image :src="person.targetUserImg===''?'../../static/images/boy.png':person.targetUserImg" mode="widthFix"></image>
						<view class="text">
							<view>{{person.targetUserName}}</view>
							<view class="tel">{{person.targetUserPhone}}</view>
						</view>
					</view>
					<view class="btn" @click="cancelBind">解绑</view>
				</view>
			</view>
			<!--  -->
			<view v-if="!person" class="no-person">
				当前暂无共同关注者
			</view>
		</view>
		
		<e-modal :visible.sync="visible">
		  <view class="modal-content">
			  <view>邀请码：{{code}}</view>
		  </view>
		  <view class="modal-btns" >
			  <view class="cancel" @click="toggleModal">取消</view>
			  <button open-type="share" class="confirm">立即分享</button>
		  </view>
		</e-modal>
		<my-toast :title="toastTitle" ref="toast" :loadingType="toastType" type="rectangle" bg-color="#ffffff" ft-color="#666666"/>
		<wyb-loading title="请稍后" ref="loading" type="rectangle" bg-color="#ffffff" ft-color="#666666"/>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				toastType:"",
				toastTitle:"",
				visible:false,
				code:'',
				person:undefined
			};
		},
		methods:{
			toggleModal(){
				this.visible ? this.visible = false : this.visible = true;
			},
			// 获取分享码
			share(){
				if(!this.person){
					this.$refs.loading.showLoading() // 显示
					this.myRequest('gmt/api/userFollow/generateCode').then(res => {
						this.$refs.loading.hideLoading();
						this.toggleModal();
						this.code = res.data.data;
					}) 
				}else{
					this.toastType = "none";
					this.toastTitle = "只能添加一个关注者哦！"; 
					this.$refs.toast.showLoading(); // 显示
				}
			},
			// 解绑
			cancelBind(){
				uni.showModal({
				    title: '解除关系',
				    content: '确认解除此关系？',
					confirmColor:'#F49D1A',
				    success:res => {
				        if (res.confirm) {
				           this.$refs.loading.showLoading() // 显示
				           this.myRequest('gmt/api/userFollow/unbundling').then(res => {
				           	this.$refs.loading.hideLoading();
				           	if(res.data.code===0){
				           		this.toastType = "ring";
				           		this.toastTitle = "解除成功";
				           		this.$refs.toast.showLoading() // 显示
				           		this.person = null;
				           		this.getBindPerson();
				           	}else{
				           		this.toastType = "error";
				           		this.toastTitle = "解除失败";
				           		this.$refs.toast.showLoading() // 显示
				           	}
				           }) 
				           
				        } 
				    }
				});

			},
			// 获取绑定人
			getBindPerson(){
				this.$refs.loading.showLoading() // 显示
				this.myRequest('/gmt/api/userFollow/findGmtUserFollowInfo').then(res => {
					this.$refs.loading.hideLoading();
					this.person = res.data.data;
				}) 
			}
		},
		onShareAppMessage(res) {
			//转发时携带 shareTicket才能在回调中获取到shareTickets
			uni.showShareMenu({
				withShareTicket: true
			})
			this.visible = false;
			return {
				title:"和我一起关注孩子成长吧",
				path: "/pages/index/index?code="+this.code,// 路径，传递参数到指定页面。
				desc:"关注孩子成长，关注儿童健康",
				imageUrl:"https://level8cases.oss-cn-hangzhou.aliyuncs.com/eca9b36c-7350-4c90-8fde-1327b4d808ff.jpg"
			}
		},
		onLoad(){
			this.getBindPerson();
		}
	}
</script>

