		.no-person{
			font-size: 58upx;
			// font-weight: bold;
			text-align: center;
			letter-spacing: 4upx;
			padding: 20upx 0;
		}
		.title{
			font-size: 30upx;
			font-weight: bold;
			letter-spacing: 2upx;
			text-align: center;
		}
		.list{
			margin-top: 40upx;
			.item{
				.left{
					display: flex;
					align-items: center;
					image{
						width: 90upx;
						height: 90upx;
						margin-right: 20upx;
					}
					.text{
						font-size: 24upx;
						.tel{
							margin-top: 6upx;
							color: #666;
						}
					}
					
				}
				.btn{
					background: linear-gradient(left,rgb(255,169,41),rgb(255,131,31));
					box-shadow: 0 4upx 16upx 0upx rgb(255,155,31);
					padding: 16upx 36upx;
					color: #fff;
					letter-spacing: 2upx;
					font-size: 26upx;
					border-radius: 32upx;
				}
			}
		}
	}
	@btnH:80upx;
	.modal-content{
		padding: 60upx 40upx;
